"use strict";


import React from 'react';
import logo from './logo.svg';
import './App.css';

const routerUsuario = require("./routers/routerUsuario");
const path = require("path");
const express = require("express");
const bodyParser = require("body-parser");

const app = express();
app.use(bodyParser.urlencoded({extended:true}));

// Rutas para usar los router
app.use("/usuario", routerUsuario);


import ListaGrupo from './Components/ListaGrupo';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="localhost:3000/usuario/grupo/3"
          target="_blank"
          rel="noopener noreferrer"
        >
          Buscar alumnos del grupo 3
        </a>
        <ListaGrupo/>
      </header>
    </div>
  );
}

app.use(middlewareNotFoundError);
app.use(middlewareServerError);

function middlewareNotFoundError(request, response) {
    // C贸digo 404: User error
    response.status(404);
/*
    response.render("404", {
            mensaje: "La pagina " + request.url + " no existe..."
    });
*/
}

function middlewareServerError(error, request, response, next) {
    // C贸digo 500: Internal server error
    response.status(500);
    console.log(error.message);
    console.log(error.stack);
/*
    response.render("500", {
            mensaje: error.message,
            pila: error.stack
    });
*/
}


app.listen(3000, function(err) {
    if (err) {
        console.error("No se pudo inicializar el servidor: "
        + err.message);
    } else {
        console.log("Servidor arrancado en el puerto 3000");
    }
});

export default App;
