import React, { Component } from "react";
import ReactDOM from "react-dom";
import logo from './logo.svg';
import './App.css';

import ListaGrupo from './Components/ListaGrupo';

class App extends Component {
    constructor() {
      super();
      this.state = { data: [] };
    }

    async cargarEstudiantesDelGrupo() {
      const response = await fetch('http://localhost:3000/usuario/grupo/3');
      const datos = await response.json();
      this.setState({estudiantes: datos, loading: false});
    }

  render() {
    this.cargarEstudiantesDelGrupo();
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>

          <tbody>
            {
            estudiantes.map(estudiante => 
              <tr key={estudiante.id}>
                <td> {estudiante.nombre}</td>
                <td> {estudiante.apellidos}</td>

              </tr>
            )
            }
          </tbody>

          <ListaGrupo/>
        </header>
      </div>
    );
  }


}
export default App;
