/*
*  Name_file :GroupStudent.js
*  Description: Pagina del grupo seleccionado por estudiante, contiene la vista del grupo seleccionado por estudiante
*    
*/
import React, { Component } from 'react';
import axios from 'axios';
import Cookies from 'universal-cookie';


const baseUrl = "http://localhost:3001/links/api/getChallenges";

const cookies = new Cookies();//cokies guarda la informacion de la sesion del usuario

class Writing extends Component {
    state = {
        data: [],

    }

    /*Si vuelvo a la pagina de login, comprueba si el usuario ya inicio sesion anteriomente
    si es el caso lo redirige a la home segun su rol*/
    componentDidMount() 
    {
        this.peticionGet();
        if (!cookies.get('correo'))
        {
            window.location.href = "./";
        }
    }

    render() {
        console.log('id: ' + cookies.get('id')); //mensaje por consola navegador
        console.log('correo: ' + cookies.get('correo'));
        console.log('nombre: ' + cookies.get('nombre'));
        console.log('apellidos: ' + cookies.get('apellidos'));
        console.log('foto: ' + cookies.get('foto'));
        console.log('activo: ' + cookies.get('activo'));
        console.log('rol: ' + cookies.get('rol'));
        console.log('grupoSeleccionado: ' + cookies.get('groupSelect'));
        return (
            <>
                <h2> Crear Writing</h2>
                <nav>
                    <button onClick={() => this.cerrarSesion()}>Cerrar Sesi√≥n</button>
                </nav>
                <div>

                    <div>
                        {this.state.data.map(challenge => {
                            return (
                                <div>
                                    <h1>{challenge.titulo} </h1>
                                    <p> {challenge.description}</p>
                                </div>

                            )
                        })}

                    </div>

                </div>
            </>
        );
    }

}


export default Writing;
