import React, { useState, Component } from 'react';
import Axios from 'axios';

const baseUrl="http://localhost:3001/links/api/get";

class Login extends Component {

    state={
        form:{
            username: '',
            password: ''
        }
    }

    handleChange = async e => {
        await this.setState({
            form: {
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        });
        console.log(this.state.form);
    }
    
    iniciarSesion=async()=>{
        await Axios.get(baseUrl, {params: {nombree: this.state.form.username, password: this.state.form.password}})
        .then(response=>{
            //return response.data;
            console.log(response.data);
        })
      
        .catch(error=>{
            console.log(error);
        })

    }


    render() {
        return (
            <div className='container'>
                <h1>Login</h1>
                <label>Usuario: </label>
                <br />
                <input type="text" name="username" onChange={this.handleChange} />
                <br />
                <label>Password: </label>
                <br />
                <input type="text" name="password" onChange={this.handleChange} />
                <br />
                <button text='Log In' onClick={()=> this.iniciarSesion()}>Log in  </button>

            </div>
        );
    }
}

export default Login;
