import React, { Component } from 'react';
import Axios from 'axios';
import Cookies from 'universal-cookie';


const baseUrl="http://localhost:3001/links/api/login";

const cookies = new Cookies();

class Login extends Component {
    state={
        form:{
            username: '',
            password: ''
        }
    }

    handleChange = async e => {
        await this.setState({
            form: {
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        });
        console.log(this.state.form);
    }
    
    iniciarSesion=async()=>{
        await Axios.get(baseUrl, {params: {nombre: this.state.form.username, password: this.state.form.password}})
        .then(response=>{
            //console.log(response.data);
            return response.data;
            
        })
        
        .then(response=>{
            if(response.length>0){//si ha encontrado a un usuario, >0
                var respuesta=response[0];//donde se guarda el objeto
                cookies.set('id', respuesta.id, {path: "/"});
                cookies.set('apellido_paterno', respuesta.apellido_paterno, {path: "/"});
                cookies.set('apellido_materno', respuesta.apellido_materno, {path: "/"});
                cookies.set('nombre', respuesta.nombre, {path: "/"});
                cookies.set('username', respuesta.username, {path: "/"});
                alert(`Bienvenido ${respuesta.nombre} ${respuesta.apellido_paterno}`);
                window.location.href="./menu";
            }else{
                alert('El usuario o el password no son correctos');
            }
        })


        .catch(error=>{
            console.log(error);
        })

    }


    render() {
        return (
            <div className='container'>
                <h1>Login</h1>
                <label>Usuario: </label>
                <br />
                <input type="text" name="username" onChange={this.handleChange} />
                <br />
                <label>Password: </label>
                <br />
                <input type="text" name="password" onChange={this.handleChange} />
                <br />
                <button text='Log In' onClick={()=> this.iniciarSesion()}>Log in  </button>

            </div>
        );
    }
}

export default Login;
