/*
*  Name_file :GroupStudent.js
*  Description: Pagina del grupo seleccionado por estudiante, contiene la vista del grupo seleccionado por estudiante
*    
*/
import React, { Component } from 'react';
import axios from 'axios';
import Cookies from 'universal-cookie';


const baseUrl = "http://localhost:3001/student/postWriting";

const baseUrl2 = "http://localhost:3001/student/getChallenge";
//<img src= {challenge.imagen}/>

const RNFetchBlob = require('react-native-fetch-blob').default;

const cookies = new Cookies();//cokies guarda la informacion de la sesion del usuario

class Writing extends Component {

   


    prueba = (uri_attachment, mimetype_attachment) => {
        return new Promise((RESOLVE, REJECT) => {
    
            // Fetch attachment
            RNFetchBlob.fetch('GET', config.apiRoot+'/app/'+uri_attachment)
              .then((response) => {
        
                let base64Str = response.data;
                var imageBase64 = 'data:'+mimetype_attachment+';base64,'+base64Str;
                // Return base64 image
                RESOLVE(imageBase64)
             })
        
           }).catch((error) => {
           // error handling
           console.log("Error: ", error)
         });
        
    }











    state = {
        data: [],
        form: {
            id: '',
            nombre: '',
            escrito: '',
            colaborativo: ''
        }

    }

    peticionGet = () => {
        axios.get(baseUrl2, { params: { idChallenge: cookies.get('challengeSelect') } }).then(response => {
            console.log(response.data);//muestra consola navegador
            this.setState({ data: response.data });
        }).catch(error => {
            console.log(error.message);
        })
    }

   
    /*Se hacen peticiones al servidor para que me devuelva la tabla grupoestudiante*/
    sendWriting = () => {
        window.location.href = './groupStudent';
        axios.post(baseUrl,{idChallenge: cookies.get('challengeSelect'), idEscritor: cookies.get('id'), escrito: this.state.form.escrito} ).then(response => {
           
            
        }).catch(error => {
            console.log(error.message);
        })
    }

    /*Lo que escribamos en el input lo guarda en el state async para que lo veamos en tiempo real */
    handleChange = async e => {
        await this.setState({
            form: {
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        });
        console.log(this.state.form);//visualizar consola navegador lo que escribimos en el input
    }


 

    /*Elimina los datos de sesion almacenada por las cookies*/
    cerrarSesion = () => {
        cookies.remove('id', { path: "/" });
        cookies.remove('correo', { path: "/" });
        cookies.remove('nombre', { path: "/" });
        cookies.remove('apellidos', { path: "/" });
        cookies.remove('foto', { path: "/" });
        cookies.remove('activo', { path: "/" });
        cookies.remove('rol', { path: "/" });
        window.location.href = './';
    }

    /*Si vuelvo a la pagina de login, comprueba si el usuario ya inicio sesion anteriomente
    si es el caso lo redirige a la home segun su rol*/
    componentDidMount() {
        this.peticionGet();
        if (!cookies.get('correo')) 
        {
            window.location.href = "./";
        }
    }
    render() {
        return (
            <>
                <h2> Crear Writing</h2>
                <nav>
                    <button onClick={() => this.cerrarSesion()}>Cerrar Sesión</button>
                </nav>
                <div>
                    <div>
                        {this.state.data.map(challenge => {
                            return (
                                <div>
                                    <h3>{challenge.titulo} </h3>
                                    <p> {challenge.descripcion}</p>
                                    {console.log(this.data)}
                                </div>
                            )
                        })}
                    </div>
                    <div>
                        <textarea name="escrito" rows="4" cols="50" onChange={this.handleChange}   ></textarea>
                        <br />
                        <br />
                        <button text='enviar' onClick={() => this.sendWriting()}> enviar  </button>
                    </div>

                </div>
            </>
        );
    }

}


export default Writing;
